#!/usr/bin/env python
# -*- coding: utf-8 -*-

# <bitbar.title>GitHub Notifications</bitbar.title>
# <bitbar.version>v3.0.2</bitbar.version>
# <bitbar.author>Matt Sephton, Keith Cirkel, John Flesch</bitbar.author>
# <bitbar.author.github>flesch</bitbar.author.github>
# <bitbar.desc>GitHub (and GitHub:Enterprise) notifications in your menu bar!</bitbar.desc>
# <bitbar.image>https://i.imgur.com/hW7dw9E.png</bitbar.image>
# <bitbar.dependencies>python</bitbar.dependencies>

import json
import urllib2
import os
import sys
import re
from itertools import groupby

# GitHub.com
github_api_key = os.getenv( 'GITHUB_TOKEN', 'Enter your GitHub.com Personal Access Token here...' )

# GitHub:Enterprise (optional)
enterprise_api_key = os.getenv( 'GITHUB_ENTERPRISE_TOKEN', 'Enter your GitHub:Enterprise Personal Access Token here...' )
enterprise_api_url = os.getenv( 'GITHUB_ENTERPRISE_API', 'https://github.example.com/api/v3' )

active = '#4078C0'
inactive = '#7d7d7d'

# Utility Functions

def plural( word, n ):
    return str(n) + ' ' + (word + 's' if n > 1 else word)

def get_dict_subset( thedict, *keys ):
    return dict([ (key, thedict[key]) for key in keys if key in thedict ])

def print_bitbar_line( title, **kwargs ):
    print title + ' | ' + ( ' '.join( [ '{}={}'.format( k, v ) for k, v in kwargs.items() ] ) )

def make_github_request( url, method='GET', data=None, enterprise = False ):
    try:
        api_key = enterprise_api_key if enterprise else github_api_key
        headers = {
            'Authorization': 'token ' + api_key,
            'Accept': 'application/json',
        }
        if data is not None:
            data = json.dumps(data)
            headers['Content-Type'] = 'application/json'
            headers['Contnet-Length'] = len(data)
        request = urllib2.Request( url, headers=headers )
        request.get_method = lambda: method
        response = urllib2.urlopen( request, data )
        return json.load( response ) if response.headers.get('content-length', 0) > 0 else {}
    except Exception:
        return None

def get_notifications( enterprise ):
    url = '%s/notifications' % (enterprise_api_url if enterprise else 'https://api.github.com')
    return make_github_request( url, enterprise=enterprise ) or []

def print_notifications( notifications, enterprise=False ):
    notifications = sorted( notifications, key=lambda notification: notification['repository']['full_name'] )
    for repo, repo_notifications in groupby( notifications, key=lambda notification: notification['repository']['full_name'] ):
        if repo:
            repo_notifications = list( repo_notifications )
            print_bitbar_line( title=repo )
            print_bitbar_line(
                title='{title} - Mark {count} As Read'.format( title=repo, count=len( repo_notifications ) ),
                alternate='true',
                refresh='true',
                bash=__file__,
                terminal='false',
                param1='readrepo',
                param2=repo,
                param3='--enterprise' if enterprise else None
            )
            for notification in repo_notifications:
                formatted_notification = format_notification( notification )
                print_bitbar_line( refresh='true', **get_dict_subset( formatted_notification, 'title', 'href', 'image', 'templateImage' ) )
                print_bitbar_line(
                    refresh='true',
                    title='%s - Mark As Read' % formatted_notification['title'],
                    alternate='true',
                    bash=__file__,
                    terminal='false',
                    param1='readthread',
                    param2=formatted_notification['thread'],
                    param3='--enterprise' if enterprise else None,
                    **get_dict_subset( formatted_notification, 'image', 'templateImage' )
                )

def format_notification( notification ):
    type = notification['subject']['type']
    formatted = {
        'thread': notification['url'],
        'title': notification['subject']['title'].encode('utf-8'),
        'href': notification['subject']['url'],
        'image': 'iVBORw0KGgoAAAANSUhEUgAAAA4AAAAQCAYAAAAmlE46AAAABHNCSVQICAgIfAhkiAAAAAlwSFlzAAALEwAACxMBAJqcGAAAA',
    }
    if len(formatted['title']) > 90:
        formatted['title'] = formatted['title'][:79] + '‚Ä¶'
    formatted['title'] = formatted['title'].replace('|','-')
    latest_comment_url = notification.get( 'subject', {} ).get( 'latest_comment_url', None )
    typejson = make_github_request( formatted['href'] )
    if latest_comment_url:
        formatted['href'] = ( make_github_request( latest_comment_url ) or {} ).get( 'html_url', formatted['href'] )
    # Try to hack a web-viewable URL if the last check failed
    if formatted['href']:
        formatted['href'] = re.sub( 'api\.|api/v3/|repos/', '', re.sub( '(pull|commit)s', ur'\1', formatted['href'] ) )
    if (type == 'PullRequest'):
        if typejson and typejson['merged']:
            formatted['image'] += ''
        elif typejson and typejson['state'] == 'closed':
            formatted['image'] += ''
        else:
            formatted['image'] += ''
        if typejson and typejson.get( 'user', {} ).get( 'login', None ):
            formatted['title'] += ' (by @{})'.format(typejson['user']['login'])
    elif (type == 'RepositoryInvitation'):
        formatted['image'] = '';
        formatted['templateImage'] = formatted.pop('image');
        formatted['href'] = 'https://github.com/{}/invitations'.format(notification['repository']['full_name'])
    elif (type == 'Issue'):
        if typejson and typejson['state'] == 'closed':
            formatted['image'] += ''
        else:
            formatted['image'] += ''
    elif (type == 'Commit'):
        formatted['image'] += 'HhJREFUKJHl0LEKwkAQBNCH3yIaf05S+VUqmh8ykFoUYn8WbnEc8a7XgYVlmNkdhv/EDgNmvHDFtmXq8ETCiFvsD2xqxksI9xnXB3cqxamYceHgVOpWC6JUi/QNQxj7jDsEd6wZ83KmLOId69bXzqekOeas0eiv4g3q4SY7NY1R2gAAAABJRU5ErkJggg=='
        formatted['templateImage'] = formatted.pop('image')
    elif (type == 'Release'):
        formatted['image'] += 'JdJREFUKJGl0DsKwkAUBdDTRgvFHbgmNyLY+QWzKxM/kK2kSKc70MIIQ0ziqBceA/dxinn8mSkKVMGUmH+CBWaNboQjdn2wqt97Pa8kNd5+C0O86YNdSZC34RLjCJxhHZYLXDCIxKuwTHGOwBNcm2WKUw9OcMCybZl6XjHpQOs30cB5gKNQiDPPP0WjV/a4aVwxNsNfUGce7P8k4XgVPSYAAAAASUVORK5CYII='
        formatted['templateImage'] = formatted.pop('image')
    return formatted

if len(sys.argv) > 1:
    command = sys.argv[1]
    args = sys.argv[2:]
    enterprise=False
    if ('--enterprise' in args):
        enterprise=True
        args.remove( '--enterprise' )
    if command == 'readrepo':
        url = '%s/repos/%s/notifications' % (enterprise_api_url if enterprise else 'https://api.github.com', args[0])
        print 'Marking %s as read' % url
        make_github_request( url=url, method='PUT', data={}, enterprise=enterprise )
    elif command == 'readthread':
        url = args[0]
        print 'Marking %s as read' % url
        make_github_request( url=url, method='PATCH', data={}, enterprise=enterprise )

else:
    is_github_defined = len( github_api_key ) == 40
    is_github_enterprise_defined = len( enterprise_api_key ) == 40
    github_notifications = get_notifications( enterprise=False ) if is_github_defined else []
    enterprise_notifications = get_notifications( enterprise=True ) if is_github_enterprise_defined else []
    has_notifications = len( github_notifications ) + len( enterprise_notifications )
    color = active if has_notifications else inactive

    if (has_notifications):
        print_bitbar_line(
            title=u'\u25CF'.encode( 'utf-8' ),
            color=color
        )
        print '---'
    else:
        print ''
        exit(0)

    print_bitbar_line( title='Refresh', refresh='true' )

    if is_github_defined:
        if len( github_notifications ):
            print_bitbar_line(
                title=( u'GitHub \u2014 %s' % plural( 'notification', len( github_notifications ) ) ).encode( 'utf-8' ),
                color=active,
                href='https://github.com/notifications',
            )
            print_notifications( github_notifications )
        else:
            print_bitbar_line(
                title=u'GitHub \u2014 No new notifications'.encode( 'utf-8' ),
                color=inactive,
                href='https://github.com',
            )

    if is_github_enterprise_defined:
        if len( enterprise_notifications ):
            if is_github_defined:
                print '---'
            print_bitbar_line(
                title=( u'GitHub:Enterprise \u2014 %s' % plural( 'notification', len( enterprise_notifications ) ) ).encode( 'utf-8' ),
                color=active,
                href='%s/notifications' % re.sub( '/api/v3', '',  enterprise_api_url ),
            )
            print_notifications( enterprise_notifications, enterprise=True )
        else:
            print '---'
            print_bitbar_line(
                title=u'GitHub:Enterprise \u2014 No new notifications',
                color=inactive,
            )

#!/usr/bin/env php
<?php
/**
 * <bitbar.title>Github CI Status</bitbar.title>
 * <bitbar.version>v1.0</bitbar.version>
 * <bitbar.author>Jordan Andree</bitbar.author>
 * <bitbar.author.github>jordanandree</bitbar.author.github>
 * <bitbar.desc>Displays Github Pull Request CI Check statuses</bitbar.desc>
 * <bitbar.dependencies>php</bitbar.dependencies>
 * <bitbar.abouturl>https://github.com/jordanandree/bitbar-github-ci</bitbar.abouturl>
 *
 * Icon sourced from feather icons: https://feathericons.com/
 *
 * To setup, create or edit your ~/.bitbarrc file with a new section:
 *
 * [github_ci]
 * access_token=xxx # Personal Access Token
 * username=jordanandree
 * repos[]=jordanandree/bitbar-github-ci
 * repos[]=jordanandree/dotfiles
 */

class GithubCIStatus
{
    /**
     * Default config values
     *
     * @var array
     */
    protected $default_config = [
        "hostname" => "github.com",
    ];

    /**
     * Config options sourced from ~/.bitbarrc
     *
     * @var stdClass
     */
    protected $config;

    /**
     * Status line template for each check
     *
     * @var string
     */
    protected $status_line = "%s %s | href=%s";

    /**
     * State of CI checks
     *
     * @var string
     */
    protected $state = "success";

    /**
     * Lock param for mutating state
     *
     * @var bool
     */
    protected $state_lock = false;

    /**
     * Menu bar icon
     *
     * @var string
     */
    protected $icon = "";

    /**
     * Output the Pull Request checks
     *
     * @return string
     */
    public function run()
    {
        $lines = [];

        try {
            $pull_requests = $this->searchPullRequests();

            if (empty($pull_requests)) {
                $this->sendOutput("No Pull Requests. Get to work!");
                exit;
            }

            foreach ($pull_requests as $pr) {
                $repo_name = substr($pr->repository_url, strlen($this->getConfig()->base_uri . "repos/"));

                $pr_info = $this->getPullRequest($repo_name, $pr->number);
                $status  = $this->getCommitStatus($repo_name, $pr_info->head->sha);
                $lines[] = $this->formatLine($status->state, $pr_info->title, $pr_info->html_url);

                if ($status->state !== $this->state && !$this->state_lock) {
                    $this->state = $status->state;
                    $this->state_lock = true;
                }

                foreach ($status->statuses as $check) {
                    $lines[] = "--" . $this->formatLine($check->state, $check->context, $check->target_url);
                }
            }
        } catch (RuntimeException $e) {
            $this->state = "failure";
            $this->sendOutput($e->getMessage());
            exit;
        }

        $this->sendOutput($lines);
    }

    /**
     * echo back output to bitbar
     *
     * @param string[]|string
     *
     * @return void
     */
    public function sendOutput($lines)
    {
        echo $this->statusIcon($this->state) . " | templateImage=" . $this->icon;
        echo "\n---\n";

        if (is_array($lines)) {
            echo implode($lines, "\n");
        } else {
            echo $lines . "\n";
        }
    }

    /**
     * Format a line for output
     *
     * @param mixed ...$text
     *
     * @return string
     */
    public function formatLine(...$args)
    {
        $icon = $this->statusIcon($args[0]);
        $args[0] = $icon;

        return sprintf($this->status_line, ...$args);
    }

    /**
     * Get the memoized configuration struct or set it
     *
     * @return stdClass
     *
     * @throws RuntimeException
     */
    protected function getConfig()
    {
        if (is_null($this->config)) {
            $bitbarrc = getenv('HOME') . "/.bitbarrc";

            if (!file_exists($bitbarrc)) {
                throw new RuntimeException("~/.bitbarrc is missing");
            }

            $config = parse_ini_file($bitbarrc, true);

            if (!array_key_exists("github_ci", $config)) {
                throw new RuntimeException("[github_ci] section is missing in ~/.bitbarrc");
            }

            $config = array_merge($this->default_config, $config["github_ci"]);
            $config["base_uri"] = "https://" . $config["hostname"] . "/api/v3/";
            $this->config = (object) $config;
        }

        return $this->config;
    }

    /**
     * Send a Request to the Github API
     *
     * @param string $endpoint
     * @param array $options
     *
     * @return stdClass
     *
     * @throws RuntimeException
     */
    protected function sendRequest($endpoint, $params = [])
    {
        $url = $this->getConfig()->base_uri . $endpoint;
        $params["access_token"] = $this->getConfig()->access_token;
        $url .= "?" . http_build_query($params);

        $headers = null;
        $body = null;
        $ch = curl_init();
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_URL, $url);
        $response = curl_exec($ch);

        if (!empty($response)) {
            list($headers, $body) = explode("\r\n\r\n", $response, 2);
        }

        curl_close($ch);

        if (is_null($body)) {
            throw new RuntimeException("Error making request to the Github API. Check your configuration.");
        } else {
            return json_decode($body);
        }
    }

    /**
     * Get a single Pull Request
     *
     * @param string $repo
     * @param int $id
     *
     * @return stdClass
     */
    protected function getPullRequest($repo, $id)
    {
        return $this->sendRequest("repos/$repo/pulls/$id");
    }

    /**
     * Get the status of a single Commit
     *
     * @param string $repo
     * @param string $sha
     *
     * @return stdClass
     */
    protected function getCommitStatus($repo, $sha)
    {
        return $this->sendRequest("repos/$repo/commits/$sha/status");
    }

    /**
     * Perform search for open issues by the author in the repos
     *
     * @return stdClass
     */
    protected function searchPullRequests()
    {
        $q = "state:open author:" . $this->getConfig()->username;
        foreach ($this->getConfig()->repos as $repo) {
          $q .= " repo:$repo";
        }

        return $this->sendRequest("search/issues", [
            "q" => $q,
        ])->items;
    }

    /**
     * Get the icon for the status
     *
     * @param string $status
     *
     * @return string
     */
    protected function statusIcon($status)
    {
        $map = [
            "success" => "\033[0;32m‚óè\033[0m",
            "failure" => "\033[0;31m‚óè\033[0m",
            "pending" => "\033[0;34m‚óè\033[0m",
            "warning" => "\033[0;33m‚óè\033[0m",
        ];

        return $map[$status];
    }
}

echo (new GithubCIStatus())->run();

#!/usr/bin/env node
// jshint esversion: 6, asi: true

// <bitbar.title>Github Pull requests</bitbar.title>
// <bitbar.version>v2.0</bitbar.version>
// <bitbar.author>Noam Knispel</bitbar.author>
// <bitbar.author.github>noamknispel</bitbar.author.github>
// <bitbar.desc>Get list of pull requests from Github for multiple repositories</bitbar.desc>
// <bitbar.dependencies>node.js request co bluebird</bitbar.dependencies>

// EDIT YOUR INFO BELOW:
const username = ''
const token = ''
var repos = [
//owner/repo
]
// DO NOT EDIT BELOW THIS POINT

var bluebird = require("bluebird")
var request = bluebird.promisifyAll(require("request"))
var co = require("co")

co(function* () {
  var results = yield repos.map(function(repo) { //jshint ignore:line
    var options = {
      url: 'https://api.github.com/repos/' + repo + '/pulls',
      headers: {
        'User-Agent': username + ' - bitbar'
      },
      auth: {
        user: username,
        password: token
      }
    }
    return request.getAsync(options);
  })

  var totalCount = 0
  var strings = results.reduce( (acc, response) => {
    var info = JSON.parse(response.body)
    if(info.length > 0) {
      totalCount += info.length
      acc.push(info[0].base.repo.name + " | color=#0000ff")
      info.forEach(function(pr) {
        acc.push("#" + pr.number + " " + pr.title + " (" + pr.user.login + ") | href=" + pr.html_url)
      })
    }
    return acc
  }, ['---'] )

  strings.unshift(`${totalCount} Pull Request`)
  for( var string of strings)
    console.log(string)
}).catch(function() {});

#!/usr/bin/env /usr/local/bin/node

// <bitbar.title>Github Repository Issues</bitbar.title>
// <bitbar.version>v1.0</bitbar.version>
// <bitbar.author>Tom Genoni</bitbar.author>
// <bitbar.desc>Lists all pull requests and issues, up to 100 total.</bitbar.desc>
// <bitbar.author.github>tomgenoni</bitbar.author.github>
// <bitbar.dependencies>node.js</bitbar.dependencies>

$HOSTNAME = ""; // For enterprise Github accounts, e.g., github.tinderforcats.com/api/v3
$ORG = "matryer";
$REPOSITORY = "bitbar";
$BITBAR_TITLE = "bitbar";
$GITHUB_ACCESS_TOKEN = "";

// ----------- Ignore below this line ---------------

var https = require('https');

var options = {
  host: $HOSTNAME || 'api.github.com',
  headers: {'user-agent': 'Mozilla/5.0'},
  path: '/repos/'+$ORG+'/'+$REPOSITORY+'/issues?GITHUB_ACCESS_TOKEN=' + $GITHUB_ACCESS_TOKEN + '&per_page=100'
};

https.get(options, function(res) {
  var body = '';
  res.on('data', function(data) {
    body += data;
  });
  res.on('end', function() {
    handleResponse(JSON.parse(body));
  });
});

function fixedIssues(body) {

  var arr = [];

  var closeTerms = ['close', 'closes', 'closed', 'fix', 'fixes', 'fixed', 'resolve', 'resolves', 'resolved'];

  body.map(function(issues){
    if ( issues.pull_request ) {
      var comments = issues.body;

      closeTerms.forEach(function(term) {
        var regex = new RegExp(term + " #([0-9]+)", "ig");
        var matches = comments.match(regex);
        if (matches) {
          matches.forEach(function(match) {
            var num = match.split("#")[1];
            arr.push(num);
          });
        }
      });
    }
  });

  return arr;
}

function allIssues(body) {

  var fixed = fixedIssues(body);

  var issues = body.map(function(issues){
    if ( issues.pull_request ) {
      return [issues.title, ' - #', issues.number, ' | href=', issues.html_url,'\n'].join('');
    }
  }).join('\n');

  var prs = body.map(function(issues){
    var color = "";
    if ( !issues.pull_request ) {
      if ( fixed.indexOf(issues.number.toString()) > -1 ) {
        color = "color=#aaaaaa";
      }
      return [issues.title, ' - #', issues.number,' (', issues.user.login, ') | href=', issues.html_url, ' ', color, '\n'].join('');
    }
  }).join('\n');

  return [issues, prs];
}

function issueCount(body) {
  var p = 0;
  body.map(function(issues){
    if ( issues.pull_request ) {
      p++;
    }
  });
  var i = body.length - p;
  return [i, p];
}

function handleResponse(body) {
  var countPRs = issueCount(body)[1];
  var countIssues = issueCount(body)[0];
  var listIssues = allIssues(body)[1];
  var listPRs = allIssues(body)[0];

  console.log($BITBAR_TITLE + " " + countPRs + "/" + countIssues);

  console.log("---");
  console.log("Pull Requests " + countPRs);
  console.log(listPRs);

  console.log("---");
  console.log("Issues " + countIssues);
  console.log(listIssues);

}

#!/usr/bin/env python
# -*- coding: utf-8 -*-

# <bitbar.title>Github review requests</bitbar.title>
# <bitbar.desc>Shows a list of PRs that need to be reviewed</bitbar.desc>
# <bitbar.version>v0.1</bitbar.version>
# <bitbar.author>Adam Bogda≈Ç</bitbar.author>
# <bitbar.author.github>bogdal</bitbar.author.github>
# <bitbar.image>https://github-bogdal.s3.amazonaws.com/bitbar-plugins/review-requests.png</bitbar.image>
# <bitbar.dependencies>python</bitbar.dependencies>

# ----------------------
# ---  BEGIN CONFIG  ---
# ----------------------

# https://help.github.com/articles/creating-a-personal-access-token-for-the-command-line/
ACCESS_TOKEN = ''

GITHUB_LOGIN = ''

# (optional) PRs with this label (e.g 'in progress') will be grayed out on the list
WIP_LABEL = ''

# (optional) Filter the PRs by an organization, labels, etc. E.g 'org:YourOrg -label:dropped'
FILTERS = ''

# --------------------
# ---  END CONFIG  ---
# --------------------

import datetime
import json
import os
import sys
try:
    # For Python 3.x
    from urllib.request import Request, urlopen
except ImportError:
    # For Python 2.x
    from urllib2 import Request, urlopen


DARK_MODE = os.environ.get('BitBarDarkMode')

query = '''{
  search(query: "%(search_query)s", type: ISSUE, first: 100) {
    issueCount
    edges {
      node {
        ... on PullRequest {
          repository {
            nameWithOwner
          }
          author {
            login
          }
          createdAt
          number
          url
          title
          labels(first:100) {
            nodes {
              name
            }
          }
        }
      }
    }
  }
}'''


colors = {
    'inactive': '#b4b4b4',
    'title': '#ffffff' if DARK_MODE else '#000000',
    'subtitle': '#586069'}


def execute_query(query):
    headers = {
        'Authorization': 'bearer ' + ACCESS_TOKEN,
        'Content-Type': 'application/json'}
    data = json.dumps({'query': query}).encode('utf-8')
    req = Request(
        'https://api.github.com/graphql', data=data, headers=headers)
    body = urlopen(req).read()
    return json.loads(body)


def search_pull_requests(login, filters=''):
    search_query = 'type:pr state:open review-requested:%(login)s %(filters)s' % {
        'login': login, 'filters': filters}
    response = execute_query(query % {'search_query': search_query})
    return response['data']['search']


def parse_date(text):
    date_obj = datetime.datetime.strptime(text, '%Y-%m-%dT%H:%M:%SZ')
    return date_obj.strftime('%B %d, %Y')


def print_line(text, **kwargs):
    params = ' '.join(['%s=%s' % (key, value) for key, value in kwargs.items()])
    print('%s | %s' % (text, params) if kwargs.items() else text)


if __name__ == '__main__':
    if not all([ACCESS_TOKEN, GITHUB_LOGIN]):
        print_line('‚ö† Github review requests', color='red')
        print_line('---')
        print_line('ACCESS_TOKEN and GITHUB_LOGIN cannot be empty')
        sys.exit(0)

    response = search_pull_requests(GITHUB_LOGIN, FILTERS)

    print_line('#%s' % response['issueCount'])
    print_line('---')

    for pr in [r['node'] for r in response['edges']]:
        labels = [l['name'] for l in pr['labels']['nodes']]
        title = '%s - %s' % (pr['repository']['nameWithOwner'], pr['title'])
        title_color = colors.get('inactive' if WIP_LABEL in labels else 'title')
