# { Aria2
import json
from urllib import request

class Aria2:
    def __init__(self, host, port, secret = None):
        self.url = "http://%s:%d/jsonrpc" % (host, port)
        self.secret = secret

    def call(self, method, params = None):
        if params and type(params) != list:
            raise Exception('Params should be a list')
        data_dict = {
            'jsonrpc': '2.0',
            'id': '15cm',
            'method': 'aria2.%s' % method
        }
        if self.secret:
            data_dict['params'] = ['token:%s' % self.secret]
            if params:
                data_dict['params'].extend(params)
        else:
            data_dict['params'] = params
        data = json.dumps(data_dict).encode('utf-8')
        return json.loads(request.urlopen(self.url, data).read().decode('utf-8'))['result']

    def getGlobalStat(self,):
        return self.call('getGlobalStat')

    def tellActive(self):
        return self.call('tellActive')

    def tellWaiting(self):
        return self.call('tellWaiting', [-1, 10])

    def tellStopped(self):
        return self.call('tellStopped', [-1, 10])

# }

import os

host = 'localhost'
port = 6800
secret = ''
title_format = 'üíª ‚¨áÔ∏è' + '%s/s'

def bytes_to_readable(speed_in_bytes, precision = 1):
    width = 5
    display_format = '%{0}.{1}f %s'.format(width, precision)
    if(speed_in_bytes > 1e9):
        return display_format % (speed_in_bytes / 1e9, 'GB')
    elif(speed_in_bytes > 1e6):
        return display_format % (speed_in_bytes / 1e6, 'MB')
    else:
        return display_format % (speed_in_bytes / 1e3, 'KB')

class Task:
    def __init__(self, type, path, size, completed_size, dl_speed):
        self.type = type
        self.name = os.path.basename(path)
        self.size = size
        self.percentage = completed_size / size
        self.dl_speed = dl_speed

    def __str__(self):
        stype_dict = {
            'active': 'üì∂',
            'waiting': 'üïí',
            'paused': '‚è∏',
            'complete': '‚úÖ'
        }
        name_width = 40
        progress_bar_width = 20
        progress_bar_length = int(self.percentage * progress_bar_width)

        sname = '%-{0}.{0}s'.format(name_width) % self.name
        # Padding task name with '...' if it is too long
        if len(self.name) > name_width:
            sname = sname[0:-3] + '...'
        stype = stype_dict[self.type] if self.type in stype_dict else '‚ùé'
        ssize = bytes_to_readable(self.size)
        sprogress = ('[%-{0}s]%-5.1f'.format(progress_bar_width) % \
                    ('=' * progress_bar_length, self.percentage * 100)) + '%'
        stask = '%s %s %s %s' % (sname, stype, ssize, sprogress)
        if self.type == 'active':
            sspeed = '  ‚¨áÔ∏è %s/s' % bytes_to_readable(self.dl_speed)
            stask += sspeed
        return stask

if __name__ == '__main__':
    aria2 = Aria2(host, port, secret)
    global_stat = aria2.getGlobalStat()
    [active_tasks, waiting_tasks, stopped_tasks] = [[Task(t['status'], t['files'][0]['path'],
                              int(t['totalLength']), int(t['completedLength']),
                                                                      int(t['downloadSpeed'])) for t in t_list if 'files' in t and t['files'] and int(t['totalLength']) > 0]
                        for t_list in (aria2.tellActive(), aria2.tellWaiting(), aria2.tellStopped())]
    print(title_format % bytes_to_readable(int(global_stat['downloadSpeed'])) + '|')
    print('---')
    print('Active Tasks|color="#1b42eb"')
    for task in active_tasks:
        print('%s|font="Monaco"' % task)
    print('---')
    print('---')
    print('Other Tasks|color="#7553fc"')
    print('---')
    for task in waiting_tasks :
        print('%s|font="Monaco"' % task)
    for task in stopped_tasks:
        print('%s|font="Monaco"' % task)

#!/usr/bin/env -S PATH="${PATH}:/usr/local/bin" python3

# <bitbar.title>Bandwidth.py</bitbar.title>
# <bitbar.version>v1.0</bitbar.version>
# <bitbar.author>ChTBoner</bitbar.author>
# <bitbar.author.github>chtboner</bitbar.author.github>
# <bitbar.desc>Displays upload and download speeds</bitbar.desc>
# <bitbar.image>https://i.imgur.com/qDPtOxl.png</bitbar.image>
# <bitbar.dependencies>python3, psutil</bitbar.dependencies>
#
# by ChTBoner

"""
    An implementation in python of the Bandwith Bitbar Plugin
    Should work on Linux too with Argos or other status bar (Polybar for example)
    https://getbitbar.com/plugins/Network/bandwidth.1s.sh

    Unlike the shell status bar, here he result displayed in the bar will the total of all interfaces
"""

import psutil
from time import sleep


def human_bytes(n):
    """
        convert bytes to human readable format
        'borrowed' from https://github.com/giampaolo/psutil/blob/master/scripts/ifconfig.py
    """

    symbols = ('K', 'M', 'G', 'T', 'P', 'E', 'Z', 'Y')
    prefix = {}
    for i, s in enumerate(symbols):
        prefix[s] = 1 << (i + 1) * 10

    for symbol in reversed(symbols):
        if n >= prefix[symbol]:
            value = n / prefix[symbol]
            return '{} {}'.format(round(value, 2), symbol)
    return "{} B".format(round(n, 2))


def print_stats(upload, download):
    """
    pretty prints the results
    upload and download values are multiplied by 2 to compensate 0.5 second sleep
    """
    return "‚ñº {} - {} ‚ñ≤".format(human_bytes(upload * 2), human_bytes(download * 2))


def main():
    # create dict to store results for every interface
    results = {}

    # set original data and store it in dict
    results.update(
        {'total': {
            'up': psutil.net_io_counters(pernic=False).bytes_sent,
            'down': psutil.net_io_counters(pernic=False).bytes_recv}
        })

    # loops through all interfaces to store upload and download data
    for interface in psutil.net_io_counters(pernic=True):
        results.update(
            {interface: {
                'up': psutil.net_io_counters(pernic=True)[interface].bytes_sent,
                'down': psutil.net_io_counters(pernic=True)[interface].bytes_recv}
            })

    sleep(0.5)

    # after sleeping, updates the data to be the difference between the two iterations
    results.update(
        {'total': {
            'up': psutil.net_io_counters(pernic=False).bytes_sent - results["total"]["up"],
            'down': psutil.net_io_counters(pernic=False).bytes_recv - results["total"]["down"]}
        })

    # iterates over interfaces to update data
    for interface in results:
        if interface != 'total':
            results.update({
                interface: {
                    'up': psutil.net_io_counters(pernic=True)[interface].bytes_sent - results[interface]["up"],
                    'down': psutil.net_io_counters(pernic=True)[interface].bytes_recv - results[interface]["down"]}
            })

    # print the result to the bar
    print(print_stats(results["total"]["up"], results["total"]["down"]))

    print('---')
    # print in dropdown
    for interface in results:
        if interface != 'total':
            print("{}: {}".format(interface, print_stats(results[interface]["up"], results[interface]["down"])))


if __name__ == '__main__':
    main()

