"use strict";

/* jshint -W100 */
/* jshint esversion: 6 */

const https = require('https');

const ENDPOINT = 'https://www.howmanypeopleareinspacerightnow.com/peopleinspace.json';

function request(endpoint) {
    return new Promise((resolve, reject) => {
        https.get(endpoint, (res) => {
            const body = [];
            res.setEncoding('utf8');
            res.on('data', (data) => body.push(data));
            res.on('end', () => {
                try {
                    resolve(JSON.parse(body.join()));
                } catch (error) {
                    reject(error);
                }
            });
            res.on('error', (error) => {
                reject(error);
            });
        });
    });
}

function getEmojiFlag(name) {
    switch (name.toLowerCase()) {
        case 'russia': return 'üá∑üá∫';
        case 'usa': return 'üá∫üá∏';
        case 'italy': return 'üáÆüáπ';
        case 'china': return 'üá®üá≥';
        case 'japan': return 'üáØüáµ';
        case 'canada': return 'üá®üá¶';
        case 'france': return 'üá´üá∑';
        case 'germany': return 'üá©üá™';
        case 'belgium': return 'üáßüá™';
        case 'netherlands': return 'üá≥üá±';
        case 'sweden': return 'üá∏üá™';
    }
}

request(ENDPOINT).then((json) => {
    console.log(`üë®üèª‚ÄçüöÄ ${json.number}`);
    console.log(`---`);
    json.people.forEach((person) => {
        console.log(`${getEmojiFlag(person.country)} ${person.name} | href=${person.biolink} color=black`);
        console.log(`${person.title} ‚Äì ${person.launchdate.split('-').reverse().join('.')}`);
        console.log(`---`);
    });
}).catch((error) => {
    console.log(`üë®üèª‚ÄçüöÄ ?\n---`);
    console.log(`Houston, we have an error! | color=red`);
    console.log(`---`);
    console.log(error);
});

