#!/usr/bin/env /usr/local/bin/node
/*
<bitbar.title>PagerDuty Incidents</bitbar.title>
<bitbar.version>v0.2.0</bitbar.version>
<bitbar.author>Pedro Pablo Fuentes Schuster</bitbar.author>
<bitbar.author.github>pedrofuentes</bitbar.author.github>
<bitbar.desc>Shows all the active incidents grouped by Service. For installation instructions check https://github.com/PedroFuentes/bitbar-plugins/blob/master/pagerDutyIncidents/README.md</bitbar.desc>
<bitbar.image>http://cdn.pedrofuent.es/images/github/PagerDutyIncidents_Screenshot.png</bitbar.image>
<bitbar.dependencies>node, npm/node-fetch, npm/time-ago, npm/bitbar, npm/home-config</bitbar.dependencies>
<bitbar.abouturl>https://github.com/PedroFuentes/bitbar-plugins/tree/master/pagerDutyIncidents</bitbar.abouturl>
*/
/* MIT Licensed https://opensource.org/licenses/MIT */
/* jshint esversion: 6 */

'use strict';

const fetch = require('node-fetch');
const ta = require('time-ago')();
const bitbar = require('bitbar');

const cfg = require('home-config').load('.bitbarrc');

if (!cfg.pagerdutyincidents || !cfg.pagerdutyincidents['api.endpoint'] || !cfg.pagerdutyincidents['api.token']) {
  const json = [];

  json.push({
    text: 'Config Needed',
    dropdown: false,
  },
  bitbar.sep, {
    text: 'Add to your .bitbarrc config file on your',
  }, {
    text: 'home directory the following information:',
  },
  bitbar.sep, {
    text: '[pagerdutyincidents]',
  }, {
    text: 'api.endpoint=https://',
  }, {
    text: 'api.token={your-token}',
  });

  bitbar(json);
  process.exit();
}

const config = {
  api: {
    endpoint: cfg.pagerdutyincidents['api.endpoint'],
    token: cfg.pagerdutyincidents['api.token'],
    query: cfg.pagerdutyincidents['api.query'] ? cfg.pagerdutyincidents['api.query'] : 'limit=100&statuses[]=triggered&statuses[]=acknowledged',
  },
  icon: {
    active: '',
    inactive: '',
  },
  colors: {
    critical: '#FF0000',
    warning: '#999900',
    regularText: '#808080',
  },
};

// TODO: load all available pages
fetch(`${config.api.endpoint}/incidents?${config.api.query}`, {
  method: 'GET',
  headers: {
    'Content-Type': 'application/json',
    Authorization: `Token token=${config.api.token}`,
  },
})
  .then((res) => res.json())
  .then((json) => {
  // Group incidents by Service
    const serviceIncidents = [];

    json.incidents.forEach((incident) => {
      if (!serviceIncidents[incident.service.id]) {
        serviceIncidents[incident.service.id] = {
          name: incident.service.summary,
          html_url: incident.service.html_url,
          incidents: [],
        };
      }

      serviceIncidents[incident.service.id].incidents.push(incident);
    });

    return {
      serviceIncidents,
      total: json.incidents.length,
    };
  })
  .then((obj) => {
    const json = [];
    const { serviceIncidents } = obj;

    json.push({
      text: `[${obj.total}]`,
      dropdown: false,
      templateImage: config.icon.inactive,
      size: 8,
    },
    bitbar.sep);

    if (Object.keys(serviceIncidents).length) {
      Object.keys(serviceIncidents).forEach((prop) => {
        const incidents = [];

        serviceIncidents[prop].incidents.forEach((incident) => {
          const assignedTo = [];

          incident.assignments.forEach((user) => {
            assignedTo.push({
              text: `${user.assignee.summary}, ${ta.ago(new Date(Date.parse(user.at)))}`,
              href: user.assignee.html_url,
              color: config.colors.regularText,
            }, {
              text: user.assignee.html_url,
              alternate: true,
            });
          });

          incidents.push({
            text: incident.title,
            length: 50,
            href: incident.html_url,
            color: incident.status === 'triggered' ? config.colors.critical : config.colors.warning,
          }, {
            text: incident.title,
            alternate: true,
          }, {
            text: 'Assigned To',
            submenu: assignedTo,
          }, {
            text: `Created\t\t: ${ta.ago(new Date(Date.parse(incident.created_at)))}`,
            color: config.colors.regularText,
          }, {
            text: `Created at\t: ${incident.created_at}`,
            alternate: true,
            color: config.colors.regularText,
          }, {
            text: `Status\t\t: ${incident.status}`,
            color: config.colors.regularText,
          }, {
            text: `Urgency\t\t: ${incident.urgency}`,
            color: config.colors.regularText,
          }, {
            text: `Escalations\t: ${incident.alert_counts.all}`,
            color: config.colors.regularText,
          });
        });

        json.push({
          text: `${serviceIncidents[prop].name} (${serviceIncidents[prop].incidents.length})`,
          href: serviceIncidents[prop].html_url,
          submenu: incidents,
        },
        bitbar.sep);
      });
    } else {
      json.push({
        text: 'No Open Incidents',
      });
    }

    bitbar(json);
  });

#!/usr/bin/env /usr/local/bin/node
/*
<bitbar.title>PagerDuty On-Call</bitbar.title>
<bitbar.version>v0.2.0</bitbar.version>
<bitbar.author>Pedro Pablo Fuentes Schuster</bitbar.author>
<bitbar.author.github>pedrofuentes</bitbar.author.github>
<bitbar.desc>Shows who is on call for all the active Escalation Policies and what services have active incidents. For installation instructions check https://github.com/PedroFuentes/bitbar-plugins/blob/master/pagerDutyOnCall/README.md</bitbar.desc>
<bitbar.image>http://cdn.pedrofuent.es/images/github/PagerDutyOnCall_Screenshot.png</bitbar.image>
<bitbar.dependencies>node, npm/node-fetch, npm/time-ago, npm/bitbar, npm/home-config</bitbar.dependencies>
<bitbar.abouturl>https://github.com/PedroFuentes/bitbar-plugins/tree/master/pagerDutyOnCall</bitbar.abouturl>
*/
/* MIT Licensed https://opensource.org/licenses/MIT */
/* jshint esversion: 6 */

'use strict';

const fetch = require('node-fetch');
const ta = require('time-ago')();
const bitbar = require('bitbar');

const cfg = require('home-config').load('.bitbarrc');

if (!cfg.pagerdutyoncall || !cfg.pagerdutyoncall['api.endpoint'] || !cfg.pagerdutyoncall['api.token']) {
  const json = [];

  json.push({
    text: 'Config Needed',
    dropdown: false,
  },
  bitbar.sep, {
    text: 'Add to your .bitbarrc config file on your',
  }, {
    text: 'home directory the following information:',
  },
  bitbar.sep, {
    text: '[pagerdutyoncall]',
  }, {
    text: 'api.endpoint=https://',
  }, {
    text: 'api.token={your-token}',
  }, {
    text: 'api.query={your-filter-query} *optional',
  }, {
    text: 'style.prefix={service-name-prefix} *optional',
  });

  bitbar(json);
  process.exit();
}

const config = {
  api: {
    endpoint: cfg.pagerdutyoncall['api.endpoint'],
    token: cfg.pagerdutyoncall['api.token'],
    query: cfg.pagerdutyoncall['api.query'] ? cfg.pagerdutyoncall['api.query'] : '',
  },
  icon: {
    active: '',
    inactive: '',
  },
  colors: {
    critical: '#FF0000',
    warning: '#999900',
    regularText: '#808080',
    onCallText: !process.env.BitBarDarkMode || process.env.BitBarDarkMode === 0 ? '#000000' : '#FFFFFF',
  },
  style: {
    indentation: '      ',
    prefix: cfg.pagerdutyoncall['style.prefix'] ? cfg.pagerdutyoncall['style.prefix'] : '',
  },
};

// TODO: Add support more than 100 escalations
fetch(`${config.api.endpoint}/escalation_policies?limit=100&include[]=services&include[]=teams&include[]=oncall&query=${config.api.query}`, {
  method: 'GET',
  headers: {
    'Content-Type': 'application/json',
    Authorization: `Token token=${config.api.token}`,
  },
})
  .then((res) => res.json())
  .then((json) => {
    const escalations = [];
    let activeIncident = false;

    json.escalation_policies.forEach((escalation) => {
      let activeServiceIncident = false;
      const services = [];
      let activeServices = 0;
      const htmlDomain = getHostName(escalation.html_url);

      escalation.services.forEach((service) => {
        if (service.status !== 'disabled') {
          activeServices += 1;

          if (service.status !== 'active') {
            activeIncident = true;
            activeServiceIncident = true;

            services.push({
              text: `${config.style.indentation}${service.status === 'maintenance' ? ':construction:' : ':bangbang:'} ${cleanName(service.name)}, ${ta.ago(new Date(Date.parse(service.last_incident_timestamp)))}`,
              trim: false,
              color: service.status === 'critical' ? config.colors.critical : config.colors.warning,
              href: `${service.html_url}`,
            });
          }
        }
      });

      if (activeServices) {
        const onCallList = [];

        escalation.on_call.forEach((onCall) => {
          onCallList.push({
            text: `${onCall.level}. ${onCall.user.name}`,
            color: config.colors.onCallText,
            href: `https://${htmlDomain}/users/${onCall.user.id}`,
          }, {
            text: `${onCall.level}. ${onCall.user.email}`,
            alternate: true,
          });
        });

        escalations.push({
          text: `${activeServiceIncident ? ':sos:' : ':cool:'} ${cleanName(escalation.name)}`,
          href: `${escalation.html_url}`,
          submenu: onCallList,
        });

        if (services.length) escalations.push(services[0]);

        escalations.push(bitbar.sep);
      }
    });

    escalations.unshift({
      text: '☎',
      dropdown: false,
      templateImage: activeIncident ? config.icon.active : config.icon.inactive,
      size: 8,
    },
    bitbar.sep);

    bitbar(escalations);
  });

function cleanName(name) {
  return name.replace(`${config.style.prefix} - `, '').replace(config.style.prefix, '').trim();
}

function getHostName(url) {
  const match = url.match(/:\/\/(www[0-9]?\.)?(.[^/:]+)/i);
  if (match != null && match.length > 2 && typeof match[2] === 'string' && match[2].length > 0) {
    return match[2];
  }
  return null;
}
