#!/usr/bin/env php
<?php
// <bitbar.title>BitBar Plugins Installer</bitbar.title>
// <bitbar.version>v1.0</bitbar.version>
// <bitbar.author>Ale≈° Farƒçnik</bitbar.author>
// <bitbar.author.github>alesf</bitbar.author.github>
// <bitbar.desc>Quickly install bitbar plugins.</bitbar.desc>
// <bitbar.image>http://i.imgur.com/Qn8TJ60.png</bitbar.image>
// <bitbar.dependencies>php >= 5</bitbar.dependencies>

define('CAT_URL', 'https://api.github.com/repos/matryer/bitbar-plugins/git/trees/master?recursive=1');
define('UA', 'Mozilla/4.0 (compatible; MSIE 7.0; Windows NT 5.1; '.
    '.NET CLR 1.0.3705; .NET CLR 1.1.4322; Media Center PC 4.0)');

function curl($url)
{
    $ch = curl_init();
    curl_setopt($ch, CURLOPT_URL, $url);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
    curl_setopt($ch, CURLOPT_USERAGENT, UA);
    $output = curl_exec($ch);
    curl_close($ch);
    return json_decode($output);
}

function bitbar_link($name, $path)
{
    return "bitbar://openPlugin?title={$name}&".
        "src=https://github.com/matryer/bitbar-plugins/raw/master/{$path}";
}

echo "BitBar Plugins\n";
echo "---\n";

$cats = curl(CAT_URL);
$cats = isset($cats->tree) && is_array($cats->tree) ? $cats->tree : array();

array_map(function ($item) {

    $level = substr_count($item->path, '/');
    $level_dash = str_repeat('--', $level);
    $sub_start = strrpos($item->path, '/') ? strrpos($item->path, '/')+1 : 0;
    $name = substr($item->path, $sub_start);

    if ($item->type == 'tree') {
        if ($name != 'Enabled') {
            echo "{$level_dash}{$name}\n";
        }
    } elseif ($item->type == 'blob' && $level > 0 && $name[0] != '.') {
        $link = bitbar_link($name, $item->path);
        echo "{$level_dash}{$name} | href={$link}\n";
    }
}, $cats);

#!/usr/bin/php
<?php
// <bitbar.title>Catchpoint Alerts</bitbar.title>
// <bitbar.version>v1.1</bitbar.version>
// <bitbar.author>Robert Castley</bitbar.author>
// <bitbar.author.github>rcastley</bitbar.author.github>
// <bitbar.desc>Display Catchpoint Alerts via the Pull API.</bitbar.desc>
// <bitbar.image>http://i.imgur.com/xgkKWjy.png</bitbar.image>
// <bitbar.dependencies>php, cURL</bitbar.dependencies>
// <bitbar.abouturl>http://www.catchpoint.com/</bitbar.abouturl>
echo "| \n";
echo "---\n";

$key    = 'Catchpoint_Key';
$secret = 'Catchpoint_Secret';

$data = array('grant_type' => 'client_credentials', 'client_id' => $key, 'client_secret' => $secret);

$ch = curl_init();

curl_setopt($ch, CURLOPT_URL, 'https://io.catchpoint.com/ui/api/token');
curl_setopt($ch, CURLOPT_POSTFIELDS, http_build_query($data));    
curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

$result = curl_exec($ch);

curl_close($ch);

$result = json_decode($result);

$_SESSION['token'] = base64_encode($result->access_token);

$ch = curl_init();

curl_setopt($ch, CURLOPT_URL, 'https://io.catchpoint.com/ui/api/v1/alerts?pageSize=5');
curl_setopt($ch, CURLOPT_HTTPHEADER, 
    array('Authorization: Bearer ' . $_SESSION['token']));

curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);

$data = curl_exec($ch);

curl_close($ch);

$alerts = json_decode($data);

if ($alerts != null) {
	foreach ($alerts->alerts as $a) {
		switch ($a->level->id) {
			case 0:
				$color = '#ff8000';
				$state = 'Warning';
				$emoji = ':neutral_face:';
				break;
			case 1:
				$color = '#ff0000';
				$state = 'Critical';
				$emoji = ':worried:';
				break;
			case 3:
				$color = '#80ff00';
				$state = 'OK';
				$emoji = ':smile:';
				break;
		}
		echo ":clock1030: " . $a->report_time . " | size=10 color=" . $color. "\n";
		echo $emoji . " Test: " . $a->test->name . " - " . $state . "| size= 10 color=" . $color . " href=http://portal.catchpoint.com/ui/Content/Charts/Performance.aspx?tList=" . $a->test->id . "&z=&chartView=1\n";
		echo "Reason: " . $a->alert_type->name . " | size=10 color=#000000\n";
		echo "---\n";
	}
} else {
	echo ":smile: No recent alerts | size=14 color=\"#80ff00\"";
}
//echo exec("osascript -e 'display notification \"" . $a->report_time . " - " . $a->alert_type->name . "\" with title \"" . $a->test->name . " - " . $state . "\"'");

#!/usr/bin/env /usr/local/bin/node
/*
<bitbar.title>PagerDuty Incidents</bitbar.title>
<bitbar.version>v0.2.0</bitbar.version>
<bitbar.author>Pedro Pablo Fuentes Schuster</bitbar.author>
<bitbar.author.github>pedrofuentes</bitbar.author.github>
<bitbar.desc>Shows all the active incidents grouped by Service. For installation instructions check https://github.com/PedroFuentes/bitbar-plugins/blob/master/pagerDutyIncidents/README.md</bitbar.desc>
<bitbar.image>http://cdn.pedrofuent.es/images/github/PagerDutyIncidents_Screenshot.png</bitbar.image>
<bitbar.dependencies>node, npm/node-fetch, npm/time-ago, npm/bitbar, npm/home-config</bitbar.dependencies>
<bitbar.abouturl>https://github.com/PedroFuentes/bitbar-plugins/tree/master/pagerDutyIncidents</bitbar.abouturl>
*/
/* MIT Licensed https://opensource.org/licenses/MIT */
/* jshint esversion: 6 */

'use strict';

const fetch = require('node-fetch');
const ta = require('time-ago')();
const bitbar = require('bitbar');

const cfg = require('home-config').load('.bitbarrc');

if (!cfg.pagerdutyincidents || !cfg.pagerdutyincidents['api.endpoint'] || !cfg.pagerdutyincidents['api.token']) {
  const json = [];

  json.push({
    text: 'Config Needed',
    dropdown: false,
  },
  bitbar.sep, {
    text: 'Add to your .bitbarrc config file on your',
  }, {
    text: 'home directory the following information:',
  },
  bitbar.sep, {
    text: '[pagerdutyincidents]',
  }, {
    text: 'api.endpoint=https://',
  }, {
    text: 'api.token={your-token}',
  });

  bitbar(json);
  process.exit();
}

const config = {
  api: {
    endpoint: cfg.pagerdutyincidents['api.endpoint'],
    token: cfg.pagerdutyincidents['api.token'],
    query: cfg.pagerdutyincidents['api.query'] ? cfg.pagerdutyincidents['api.query'] : 'limit=100&statuses[]=triggered&statuses[]=acknowledged',
  },
  icon: {
    active: '',
    inactive: '',
  },
  colors: {
    critical: '#FF0000',
    warning: '#999900',
    regularText: '#808080',
  },
};

// TODO: load all available pages
fetch(`${config.api.endpoint}/incidents?${config.api.query}`, {
  method: 'GET',
  headers: {
    'Content-Type': 'application/json',
    Authorization: `Token token=${config.api.token}`,
  },
})
  .then((res) => res.json())
  .then((json) => {
  // Group incidents by Service
    const serviceIncidents = [];

    json.incidents.forEach((incident) => {
      if (!serviceIncidents[incident.service.id]) {
        serviceIncidents[incident.service.id] = {
          name: incident.service.summary,
          html_url: incident.service.html_url,
          incidents: [],
        };
      }

      serviceIncidents[incident.service.id].incidents.push(incident);
    });

    return {
      serviceIncidents,
      total: json.incidents.length,
    };
  })
  .then((obj) => {
    const json = [];
    const { serviceIncidents } = obj;

    json.push({
      text: `[${obj.total}]`,
      dropdown: false,
      templateImage: config.icon.inactive,
      size: 8,
    },
    bitbar.sep);

    if (Object.keys(serviceIncidents).length) {
      Object.keys(serviceIncidents).forEach((prop) => {
        const incidents = [];

        serviceIncidents[prop].incidents.forEach((incident) => {
          const assignedTo = [];

          incident.assignments.forEach((user) => {
            assignedTo.push({
              text: `${user.assignee.summary}, ${ta.ago(new Date(Date.parse(user.at)))}`,
              href: user.assignee.html_url,
              color: config.colors.regularText,
            }, {
              text: user.assignee.html_url,
              alternate: true,
            });
          });

          incidents.push({
            text: incident.title,
            length: 50,
            href: incident.html_url,
            color: incident.status === 'triggered' ? config.colors.critical : config.colors.warning,
          }, {
            text: incident.title,
            alternate: true,
          }, {
            text: 'Assigned To',
            submenu: assignedTo,
          }, {
            text: `Created\t\t: ${ta.ago(new Date(Date.parse(incident.created_at)))}`,
            color: config.colors.regularText,
          }, {
            text: `Created at\t: ${incident.created_at}`,
            alternate: true,
            color: config.colors.regularText,
          }, {
            text: `Status\t\t: ${incident.status}`,
            color: config.colors.regularText,
          }, {
            text: `Urgency\t\t: ${incident.urgency}`,
            color: config.colors.regularText,
          }, {
            text: `Escalations\t: ${incident.alert_counts.all}`,
            color: config.colors.regularText,
          });
        });

        json.push({
          text: `${serviceIncidents[prop].name} (${serviceIncidents[prop].incidents.length})`,
          href: serviceIncidents[prop].html_url,
          submenu: incidents,
        },
        bitbar.sep);
      });
    } else {
      json.push({
        text: 'No Open Incidents',
      });
    }

    bitbar(json);
  });

#!/usr/bin/env /usr/local/bin/node
/*
<bitbar.title>PagerDuty On-Call</bitbar.title>
<bitbar.version>v0.2.0</bitbar.version>
<bitbar.author>Pedro Pablo Fuentes Schuster</bitbar.author>
<bitbar.author.github>pedrofuentes</bitbar.author.github>
<bitbar.desc>Shows who is on call for all the active Escalation Policies and what services have active incidents. For installation instructions check https://github.com/PedroFuentes/bitbar-plugins/blob/master/pagerDutyOnCall/README.md</bitbar.desc>
<bitbar.image>http://cdn.pedrofuent.es/images/github/PagerDutyOnCall_Screenshot.png</bitbar.image>
<bitbar.dependencies>node, npm/node-fetch, npm/time-ago, npm/bitbar, npm/home-config</bitbar.dependencies>
<bitbar.abouturl>https://github.com/PedroFuentes/bitbar-plugins/tree/master/pagerDutyOnCall</bitbar.abouturl>
*/
/* MIT Licensed https://opensource.org/licenses/MIT */
/* jshint esversion: 6 */

'use strict';

const fetch = require('node-fetch');
const ta = require('time-ago')();
const bitbar = require('bitbar');

const cfg = require('home-config').load('.bitbarrc');

if (!cfg.pagerdutyoncall || !cfg.pagerdutyoncall['api.endpoint'] || !cfg.pagerdutyoncall['api.token']) {
  const json = [];

  json.push({
    text: 'Config Needed',
    dropdown: false,
  },
  bitbar.sep, {
    text: 'Add to your .bitbarrc config file on your',
  }, {
    text: 'home directory the following information:',
  },
  bitbar.sep, {
    text: '[pagerdutyoncall]',
  }, {
    text: 'api.endpoint=https://',
  }, {
    text: 'api.token={your-token}',
  }, {
    text: 'api.query={your-filter-query} *optional',
  }, {
    text: 'style.prefix={service-name-prefix} *optional',
  });

  bitbar(json);
  process.exit();
}

const config = {
  api: {
    endpoint: cfg.pagerdutyoncall['api.endpoint'],
    token: cfg.pagerdutyoncall['api.token'],
    query: cfg.pagerdutyoncall['api.query'] ? cfg.pagerdutyoncall['api.query'] : '',
  },
  icon: {
    active: '',
    inactive: '',
  },
  colors: {
    critical: '#FF0000',
    warning: '#999900',
    regularText: '#808080',
    onCallText: !process.env.BitBarDarkMode || process.env.BitBarDarkMode === 0 ? '#000000' : '#FFFFFF',
  },
  style: {
    indentation: '      ',
    prefix: cfg.pagerdutyoncall['style.prefix'] ? cfg.pagerdutyoncall['style.prefix'] : '',
  },
};

// TODO: Add support more than 100 escalations
fetch(`${config.api.endpoint}/escalation_policies?limit=100&include[]=services&include[]=teams&include[]=oncall&query=${config.api.query}`, {
  method: 'GET',
  headers: {
    'Content-Type': 'application/json',
    Authorization: `Token token=${config.api.token}`,
  },
})
  .then((res) => res.json())
  .then((json) => {
    const escalations = [];
    let activeIncident = false;

    json.escalation_policies.forEach((escalation) => {
      let activeServiceIncident = false;
      const services = [];
      let activeServices = 0;
      const htmlDomain = getHostName(escalation.html_url);

      escalation.services.forEach((service) => {
        if (service.status !== 'disabled') {
          activeServices += 1;

          if (service.status !== 'active') {
            activeIncident = true;
            activeServiceIncident = true;

            services.push({
              text: `${config.style.indentation}${service.status === 'maintenance' ? ':construction:' : ':bangbang:'} ${cleanName(service.name)}, ${ta.ago(new Date(Date.parse(service.last_incident_timestamp)))}`,
              trim: false,
              color: service.status === 'critical' ? config.colors.critical : config.colors.warning,
              href: `${service.html_url}`,
            });
          }
        }
      });

      if (activeServices) {
        const onCallList = [];

        escalation.on_call.forEach((onCall) => {
          onCallList.push({
            text: `${onCall.level}. ${onCall.user.name}`,
            color: config.colors.onCallText,
            href: `https://${htmlDomain}/users/${onCall.user.id}`,
          }, {
            text: `${onCall.level}. ${onCall.user.email}`,
            alternate: true,
          });
        });

        escalations.push({
          text: `${activeServiceIncident ? ':sos:' : ':cool:'} ${cleanName(escalation.name)}`,
          href: `${escalation.html_url}`,
          submenu: onCallList,
        });

        if (services.length) escalations.push(services[0]);

        escalations.push(bitbar.sep);
      }
    });

    escalations.unshift({
      text: '‚òé',
      dropdown: false,
      templateImage: activeIncident ? config.icon.active : config.icon.inactive,
      size: 8,
    },
    bitbar.sep);

    bitbar(escalations);
  });

function cleanName(name) {
  return name.replace(`${config.style.prefix} - `, '').replace(config.style.prefix, '').trim();
}

function getHostName(url) {
  const match = url.match(/:\/\/(www[0-9]?\.)?(.[^/:]+)/i);
  if (match != null && match.length > 2 && typeof match[2] === 'string' && match[2].length > 0) {
    return match[2];
  }
  return null;
}

#!/usr/bin/env php
<?php

# <bitbar.title>Password Generator</bitbar.title>
# <bitbar.version>v1.0</bitbar.version>
# <bitbar.author>Adi</bitbar.author>
# <bitbar.author.github>gomedia-adi</bitbar.author.github>
# <bitbar.desc>Generates human-friendly strong passwords.</bitbar.desc>
# <bitbar.image>http://www.greatoceanmedia.com.au/images/63.png</bitbar.image>
# <bitbar.dependencies>php</bitbar.dependencies>
# <bitbar.abouturl>http://www.greatoceanmedia.com.au/bitbar/password-bar</bitbar.abouturl>

/*
	BitBar plugin help: https:github.com/matryer/bitbar

	Plugin updated 22/6/20

	Version history
		1.0	- also now generates a single obscure password (i.e. one that doesn't contain words)
			- length of obscure password user definable
			- changed format element "p" to "s" (coz they're symbols more than punctuation)
			- option to select subset of symbols
			- Dark Mode compatible menubar icon
		0.1	- initial release

	Credits
		- plugin inspired by a Mac app called Arcana by Tekuris
		- thanks to Christian S for v1.0 improvement suggestions
*/

/* TODO
	- format massaging/validation?
*/

define('CONFIG_FILE', "/var/tmp/bitbar.password_bar.config.php");
define('DEFAULT_FORMAT', "3,n,5,s,4");
define('FULL_SYM', "!\"#$%&'()*+,-./:;<=>?@[\]^_`{|}~");
define('PARTIAL_SYM', "!#$%&*+,-.:;=?@^_|~"); // without the quotes, brackets, slashes
define('LIMITED_SYM', "!#$%*+-=@^_"); // the supposed eBay subset
define('ALPHA_NUM', "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789");
define('DEFAULT_LENGTH', 14);

function update_config($format, $symbols, $length) {
// rewrite config file with given values

	file_put_contents(CONFIG_FILE, "<?php\n");
	file_put_contents(CONFIG_FILE, "// PASSWORD_BAR CONFIGURATION\n", FILE_APPEND);
	file_put_contents(CONFIG_FILE, "\$format = '$format';\n", FILE_APPEND);
	file_put_contents(CONFIG_FILE, "\$symbols = '$symbols';\n", FILE_APPEND);
	file_put_contents(CONFIG_FILE, "\$length = '$length';\n", FILE_APPEND);
	file_put_contents(CONFIG_FILE, "?>\n", FILE_APPEND);
}

function generate_pwd($format, $symbols) {
// generate password using format
	global $words1, $words2, $words3, $words4, $words5, $words6, $words7, $words8, $words9; // make word lists available between function calls

	$password = '';

	$symbol = FULL_SYM;
	if ($symbols == 'partial') $symbol = PARTIAL_SYM;
	if ($symbols == 'limited') $symbol = LIMITED_SYM;

	$parts = explode(',', $format);
	foreach ($parts as $part) {
		// random single digit
		if ($part == 'n')
			$password .= rand(1,9);
		// random symbol character (from predefined list)
		if (($part == 'p') || ($part == 's')) {
			$random_symbol = $symbol[rand(0, strlen($symbol)-1)];
			$password .= $random_symbol;
		}
		// random word of n letters
		if (strpos('123456789', $part) !== FALSE) {
			$words_var = 'words'.$part;
			if (!isset($$words_var)) { // create n-letter word array for first time
				$num_char = (int)$part;
				$cmd = "grep -x '.\{".$num_char."\}' /usr/share/dict/words | grep -v '[[:upper:]]'";
				$$words_var = array();
				exec($cmd, $$words_var, $err);
			}
			$words = $$words_var;
			$rand_key = array_rand($words); // get random key
			$password .= ucfirst($words[$rand_key]); // get random Word
		}
	}

	return $password;
}

function generate_obscure_pwd($length, $symbols) {
// generate an obscure password

	$password = array();

	$string = ALPHA_NUM.FULL_SYM;
	if ($symbols == 'partial') $string = ALPHA_NUM.PARTIAL_SYM;
	if ($symbols == 'limited') $string = ALPHA_NUM.LIMITED_SYM;

	$chars = str_split($string); // convert string to array
	shuffle($chars); // randomise chars
	$rand_keys = array_rand($chars, $length); // randomly pick array keys
	foreach ($rand_keys as $key) // create password from alpha/num/symbols array using random keys
		$password[] = $chars[$key];
	// rotate password chars until begins with alpha
	while (!ctype_alpha($password[0]))
		array_push($password, array_shift($password));

	return implode('', $password); // return string
}

//-------------------------------------------------------------------------------------------------

//??? CONFIG FILE

// initialise config file
if (!file_exists(CONFIG_FILE))
	touch(CONFIG_FILE);

// read config file
include(CONFIG_FILE);

// provide defaults
if (!isset($format) || $format == '') $format = DEFAULT_FORMAT;
if (!isset($symbols) || $symbols == '' || !in_array($symbols, array('full', 'partial', 'limited'))) $symbols = 'partial';
if (!isset($length) || !$length) $length = '14';

//??? MENU ACTIONS

// menu option selected
if (isset($argv[1])) {
	// prompt for readable password format & save
	if ($argv[1] == "set_format") {
		$cmd = "osascript -e 'set theString to text returned of (display dialog \"Readable Password Format \" default answer \"".$format."\" buttons {\"Cancel\", \"Save\"} default button 2)'";
		exec($cmd, $out, $err);
		if (!$err) { // something entered
			$str = implode('', $out); // text entered by user
			$format = ($str ? $str : DEFAULT_FORMAT); // reset to default if blank or zero
			update_config($format, $symbols, $length);
		}
	}
	// prompt for obscure password length & save
	if ($argv[1] == "set_length") {
		$cmd = "osascript -e 'set theString to text returned of (display dialog \"Obscure Password Length \" default answer \"".$length."\" buttons {\"Cancel\", \"Save\"} default button 2)'";
		exec($cmd, $out, $err);
		if (!$err) { // something entered
			$str = implode('', $out); // text entered by user
			$length = (int)$str; // convert str to int (will become zero if not int)
			$length = ($length ? $length : DEFAULT_LENGTH); // reset to default if blank or zero
			update_config($format, $symbols, $length);
		}
	}
	// save symbol subset
	if ($argv[1] == "set_symbols")
		update_config($format, $argv[2], $length);
	// copy password to clipboard
	if ($argv[1] == "copy_pwd") {
		$cmd = "echo '".$argv[2]."' | base64 --decode | pbcopy"; // decode & copy to clipboard
		exec($cmd, $out, $err);
	}
	// help dialog - some special chars used below to avoid CLI interpretation: backslash=‚ßµ (U+29F5); apostrophe=Ôºá(U+FF07), double quote=‚Ä≥ (U+2033)
	if ($argv[1] == "help") {
		$cmd = "osascript -e 'display dialog \"Readable Passwords Format
	1-9	- random word with the specified number of letters
		  (e.g. 3 becomes a three letter Capitalised word)
	n	- random single digit number
	s	- random symbol character (see below)

	Default format:  3,n,5,s,4
	- generates a password containing a 3 letter word,
	a digit, a 5 letter word, a symbol character,
	and finally a 4 letter word (e.g. Tig5Pesky?Muff)

Obscure Password
	This is a completely randomised string of letters,
	digits and symbols. Set the length from the menu
	(default = 14).

Symbol Sets
	Full:		!‚Ä≥#$%&Ôºá()*+,-./:;<=>?@[‚ßµ]^_`{|}~
	Partial:	!#$%&*+,-.:;=?@^_|~
	Limited:	!#$%*+-=@^_
\" buttons {\"OK\"} default button 1'";
		exec($cmd, $out, $err);
		exit;
	}
}

//??? MENUBAR

// display icon: 36x36 png (32x32max image + 2px transparent border), 144 dpi, colour mode grey, encoded using "base64 icon.png" ... prepend "|templateImage=" & append "\n"
echo "|\n";

//??? TOP-LEVEL MENU

// display readable human-friendly passwords
echo "---\n";
echo "Click to Copy:\n";
foreach (range(1, 10) as $i) {
	$pwd = generate_pwd($format, $symbols);		// generate password
 	$pwd_nobar = str_replace('|', 'ÔΩú', $pwd);	// "|" interpreted by bitbar, so substitute with a vertical line (U+FF5C)
	$pwd_enc = base64_encode($pwd);				// encode password to hide unix special characters from CLI
	echo $pwd_nobar." | terminal=false bash=\"".$argv[0]."\" param1=copy_pwd param2=".$pwd_enc." refresh=false terminal=false"."\n";
}

// display an obscure password
echo "‚Ä¶\n";
$pwd = generate_obscure_pwd($length, $symbols);
$pwd_nobar = str_replace('|', 'ÔΩú', $pwd);	// "|" interpreted by bitbar, so substitute with a vertical line (U+FF5C)
$pwd_enc = base64_encode($pwd);				// encode password to hide unix special characters from CLI
