#!/usr/local/bin/node
/*
* <bitbar.title>Github Repo Stars</bitbar.title>
* <bitbar.version>v1.0</bitbar.version>
* <bitbar.author>Varun Malhotra</bitbar.author>
* <bitbar.author.github>softvar</bitbar.author.github>
* <bitbar.image>http://varunmalhotra.xyz/media/images/bitbar-plugin-github-repos-stars.png</bitbar.image>
* <bitbar.desc>Github repos stars and Emoji notification üîî on change in stars count</bitbar.desc>
* <bitbar.dependencies>node</bitbar.dependencies>
* <bitbar.abouturl>http://varunmalhotra.xyz/blog/2016/02/bitbar-plugins-for-github-and-producthunt.html</bitbar.abouturl>
*/


// Configurable params
var config = {
    accessToken: '',
    username: 'softvar',
    hideZeroStarsRepo: false,
    filename: '/tmp/github-repo-stars.txt'
};

var https = require('https');
var fs = require('fs');

var displayInMenuBar = 'Git| color=green dropdown=false';
displayInMenuBar += '\n---\n';

function checkIfFileExists() {
    fs.exists(config.filename, function (exists) {
        if (exists) {
            return;
        } else {
            fs.writeFile(config.filename, {flag: 'wx'}, 0, function (err, data) {
                if (err) { throw err; }
            });
        }
    });
}

function sortOn(arr, key, orderBy) {
    arr.sort(function (a, b) {
        return orderBy ? b[key] - a[key] : a[key] - b[key];
    });
}

function getRepoStars(options) {
    var output = '';
    var req = https.request(options, function(res) {
        var body = '';
        res.on('data', function(chunk) {
            body += chunk;
        });
        res.on('end', function() {
            var name;
            var stars = [];
            var response = JSON.parse(body);

            // sort on stargazers_count to show repos having more stars on top
            sortOn(response, 'stargazers_count', true);

            for (var i = 0; i < response.length; i++) {
                // show only `your` repos, not the forked ones
                if (response[i].fork) { continue; }

                if (response[i].stargazers_count === 0 && config.hideZeroStarsRepo) { continue; }

                name = response[i].name;
                output += '(' + response[i].stargazers_count + ')' + name + ' | length=15 href=' + response[i].html_url;
                output += '\n---\n';
                stars.push(response[i].stargazers_count);
            }

            var oldData;
            fs.readFile(config.filename, 'utf8', function (err, data) {
                if (err) { throw err; }
                oldData = data;

                // SHow notification bell in OS X menu bar if current and revious data doesnt match
                // Voilla, someone starred your repo on Github
                if (oldData.toString() !== stars.toString()) {
                    displayInMenuBar = 'Gitüîî| color=green dropdown=false';
                    displayInMenuBar += '\n---\n';
                }

                // Write all the stars count in the file
                fs.writeFile(config.filename, stars.toString(), {flag: 'w'}, function (err) {
                    if (err) { throw err; }
                });

                // Finally log the entire output
                console.log(displayInMenuBar + output);
            });
        });
    });
    req.end();

    req.on('error', function(e) {
        console.error(e);
    });
}

checkIfFileExists();

// API call options
var options = {
    hostname: 'api.github.com',
    path: '/users/' + config.username + '/repos',
    method: 'GET',
    headers: {
        'Authorization': 'token ' + config.accessToken,
        'User-Agent': 'Awesome-Octocat-App'
    }
};

getRepoStars(options);

#!/usr/bin/env /usr/local/bin/node

// <bitbar.title>Is It Up?</bitbar.title>
// <bitbar.version>v1.1</bitbar.version>
// <bitbar.author>Zachary David Saunders</bitbar.author>
// <bitbar.author.github>ZacharyDavidSaunders</bitbar.author.github>
// <bitbar.desc>A MacOs Menu Bar (BitBar) plugin that allows you to check whether or not a website is currently up (online).</bitbar.desc>
// <bitbar.dependencies>node.js</bitbar.dependencies>
// <bitbar.image>https://i.imgur.com/BxHFJMn.png</bitbar.image>
/*jshint esversion: 6 */



//üëãüëãüëã Feel free to change the following varaibles to best suit your needs.
var upMessage = 'Yes ‚úÖ';
var unknownMessage = 'Error, click for details ‚ö†Ô∏è';
var downMessage = 'No üî•';
var website = "www.google.com";



//<--- DO NOT EDIT THE CODE BELOW THIS LINE. --->
const https = require('https');

var version = "v1.1";

renderPlugin();

function renderPlugin(){
  checkWebsite(function(status){
    var menuBarIcon = "Is \""+website+"\" up?: " + getMessage(status);
    if(status === "up"){
      console.log(menuBarIcon+" |color:green");
      console.log("---");
      console.log("Visit \""+website+"\". [Click to visit site] |href=https://"+website);
    }else if (status === "down"){
      console.log(menuBarIcon+" |color:red");
    }else{
      console.log(menuBarIcon+" |color:yellow");
      console.log("---");
      console.log("Error: Unable to contact \""+ website+"\".");
      console.log("Please verify that you entered the website correctly (remember, you don't need a www/http/https prefix).");
      console.log("If this problem persists, isitdown.site's API may be unavailable.");
    }

    console.log("---");
    console.log("Powered by \"isitdown.site\". [Click to visit site] |href=https://isitdown.site");
    console.log("For more information, please see the github repository. [Click to visit site] |href=https://github.com/ZacharyDavidSaunders/IsItUp-BitBarPlugin");
    console.log("---");
    console.log("Version: "+version+"\n¬© Zachary David Saunders 2018 |size:10");
  });
}

function checkWebsite(callback){
  var result;
  https.get('https://isitdown.site/api/'+website, (resp) => {
    var data = '';
    resp.on('data', (chunk) => {
      data += chunk;
    });

    resp.on('end', () => {
      try{
        var response = JSON.parse(data);
        if(response.isitdown == false){
          result = "up";
        }else if (response.isitdown == true){
          result = "down";
        }else{
          result = "unknown";
        }
      }catch(error){
        result = "unknown";
      }
      callback(result);
    });

  }).on("error", (err) => {
      result = "unknown";
      callback(result);
  });
}

function getMessage(status){
  if(status === "up"){
    return upMessage;
  }else if (status === "down"){
    return downMessage;
  }else{
    return unknownMessage;
  }
}

#!/usr/bin/env /usr/local/bin/node

/*
# <bitbar.title>New XKCD</bitbar.title>
# <bitbar.version>v1.0.0</bitbar.version>
# <bitbar.author>Rob Tarr</bitbar.author>
# <bitbar.author.github>robtarr</bitbar.author.github>
# <bitbar.desc>Check for new XKCD comic</bitbar.desc>
# <bitbar.dependencies>node</bitbar.dependencies>
# <bitbar.abouturl>https://github.com/robtarr/bitbar-plugins</bitbar.abouturl>
*/

const https = require("https");
const fs = require('fs');

const url = 'https://xkcd.com/atom.xml';
const icon = '| '
const errorIcon = '| ';

function isNew(xml) {
  const updatedOn = xml.match(/<updated>(.*?)Z<\/updated>.*/);
  if (!updatedOn || !updatedOn[1]) return false;

  const updatedDate = new Date(updatedOn[1]);
  return updatedDate.getDay() === new Date().getDay();
}

https.get(url, res => {
  res.setEncoding('utf8');
  let body = '';
  res.on('data', data => {
    body += data;
  });
  res.on('end', () => {
    if (isNew(body)) {
      console.log(icon);
      console.log('---');
      console.log('xkcd.com | href=http://www.xkcd.com');
    }
  });
});

